import sqlite3
import pandas as pd
import credentials
import datetime

sqlite_file = credentials.get_db_path()


def convert_date_to_timestamp(date_string: str):
    return datetime.datetime.strptime(date_string, "%Y-%m-%d").timestamp()


def load_heat_map_as_df(timestamp_from: str, timestamp_until: str):
    conn = sqlite3.connect(sqlite_file)
    return pd.read_sql_query(
        "select bounding_box.longitude, bounding_box.latitude, count() as magnitude, bounding_box.range "
        "from bounding_box inner join tweets on tweets.bounding_box_id = bounding_box.bounding_box_id "
        "where tweets.lang = 'en' or tweets.lang = 'nl' and strftime('%s', tweets.created_at) between '"
        + timestamp_from + "' and '" + timestamp_until + "' group by tweets.bounding_box_id", conn)


def load_heat_map_as_df_gmap():
    conn = sqlite3.connect(sqlite_file)
    return pd.read_sql_query(
        "select bounding_box.longitude, bounding_box.latitude, count() as magnitude, bounding_box.range "
        "from bounding_box inner join tweets on tweets.bounding_box_id = bounding_box.bounding_box_id "
        "where tweets.lang = 'en' or tweets.lang = 'nl' group by tweets.bounding_box_id", conn)


def load_heat_map_as_df_gmap2():
    conn = sqlite3.connect(sqlite_file)
    return pd.read_sql_query(
        "select bounding_box.longitude, bounding_box.latitude, count() as magnitude, bounding_box.range "
        "from bounding_box inner join tweets on tweets.bounding_box_id = bounding_box.bounding_box_id "
        "group by tweets.bounding_box_id", conn)


def load_network_graph_count(timestamp_from: str, timestamp_until: str, count_limit: int):
    conn = sqlite3.connect(sqlite_file)
    return pd.read_sql_query(
        "select user_mention.screen_name as 'mention', count(user_mention.screen_name) as 'count' from user_mention "
        "join tweets on user_mention.tweet_id = tweets.id join users on users.id = tweets.user_id "
        "where users.is_history_complete = 1 and strftime('%s', tweets.created_at) between '" + timestamp_from +
        "' and '" + timestamp_until + "' group by user_mention.screen_name having count(user_mention.screen_name) >= " +
        str(count_limit) + " order by count(user_mention.screen_name) desc", conn)


def load_network_graph_source(timestamp_from: str, timestamp_until: str, v_list):
    conn = sqlite3.connect(sqlite_file)
    lst = "('" + r"', '".join([str(a) for a in v_list]) + r"')"
    query = "select users.screen_name as 'from', user_mention.screen_name as 'to', count(user_mention.screen_name) as" \
            " 'edge_value' from users join tweets on users.id = tweets.user_id join user_mention on" \
            " tweets.id = user_mention.tweet_id where users.screen_name in " + lst + " and strftime('%s', tweets.created_at) between '" + timestamp_from + "' and '" + timestamp_until + "' and user_mention.screen_name in " + lst + " and users.screen_name is not user_mention.screen_name group by user_mention.screen_name"
    return pd.read_sql_query(query, conn)


def load_network_graph_source2(timestamp_from: str, timestamp_until: str, username: str, v_list):
    conn = sqlite3.connect(sqlite_file)
    lst = "('" + r"', '".join([str(a) for a in v_list]) + r"')"
    return pd.read_sql_query(
        "select users.screen_name as 'from', user_mention.screen_name as 'to', count(user_mention.screen_name) as 'edge_value'"
        "from users join tweets on users.id = tweets.user_id join user_mention on tweets.id = user_mention.tweet_id "
        "where users.screen_name = '" + username + "' and strftime('%s', tweets.created_at) between '" + timestamp_from +
        "' and '" + timestamp_until + "' and user_mention.screen_name in " + lst + " group by user_mention.screen_name",
        conn)


def load_latest_tweets():
    conn = sqlite3.connect(sqlite_file)
    return pd.read_sql_query(
        "select max(tweets.id) as 'last_tweet', users.screen_name, tweets.bounding_box_id from tweets join users on tweets.user_id"
        " = users.id where users.is_history_complete = 1 group by users.screen_name", conn)

